    public void DrawLine(Vector3 root, Vector3 branch, float width1, Color color1, string name, string cost, float min, float max)
    {
        BonsaiBranch = GameObject.Find("BonsaiBranch");
        GameObject branch1 = Instantiate(BonsaiBranch);

        BranchName = GameObject.Find("BranchTextObject");
        GameObject branchname1 = Instantiate(BranchName);
        CostName = GameObject.Find("CostTextObject");
        GameObject costname1 = Instantiate(CostName);
        //PieLine = GameObject.Find("PieLines");
        //GameObject pieline1 = Instantiate(PieLine);

        branch1.GetComponent<BonsaiBranch>().start = root ;
        branch1.GetComponent<BonsaiBranch>().stop = branch ;
        branch1.GetComponent<BonsaiBranch>().width = width1 / 100000f;
        Renderer rend = branch1.GetComponent<Renderer>();
        rend.material.color = color1;
        rend.material.SetColor("_EmissionColor", color1);

        branchname1.GetComponent<BonsaiLabel>().start = root ;
        branchname1.GetComponent<BonsaiLabel>().stop = branch ;
        TextMesh text1 = branchname1.GetComponent<TextMesh>();
        text1.text = name;

        costname1.GetComponent<CostLabel>().start = root;
        costname1.GetComponent<CostLabel>().stop = branch;
        TextMesh cost1 = costname1.GetComponent<TextMesh>();
        cost1.text = cost;

        //pieline1.GetComponent<PieLineRenderer>().start = min;
        //pieline1.GetComponent<PieLineRenderer>().stop = max;
        //branch1.GetComponent<BonsaiBranch>().width = 0.04f;
    
}
